mapper.select:
SELECT storeinfo.* FROM ( SELECT rownum as rnum, storeinfo.* FROM ( SELECT storeinfo.* FROM ( SELECT storeinfo.* , NVL(( SELECT avg(avgscore) FROM reviewinfo x WHERE storeinfo.storeno = x.storeno GROUP BY x.storeno ) ,0) AS AVGSCORE , NVL(( SELECT count(reviewno) FROM reviewinfo x WHERE storeinfo.storeno = x.storeno GROUP BY x.storeno ), 0) AS REVIEWCNT FROM storeinfo storeinfo WHERE 1=1
) storeinfo ORDER BY
DESC ) storeinfo
) storeinfo WHERE rnum between #{pageStart} and #{pageEnd}

SELECT count(storeno) FROM ( SELECT rownum as rnum, storeinfo.* FROM ( SELECT storeinfo.* FROM ( SELECT storeinfo.* , NVL(( SELECT avg(avgscore) FROM reviewinfo x WHERE storeinfo.storeno = x.storeno GROUP BY x.storeno ) ,0) AS AVGSCORE , NVL(( SELECT count(reviewno) FROM reviewinfo x WHERE storeinfo.storeno = x.storeno GROUP BY x.storeno ), 0) AS REVIEWCNT FROM storeinfo storeinfo WHERE 1=1
) storeinfo ORDER BY
DESC ) storeinfo ) storeinfo

SELECT storeinfo.* FROM ( SELECT rownum as rnum, storeinfo.* FROM ( SELECT storeinfo.* FROM ( SELECT storeinfo.* , NVL(( SELECT avg(avgscore) FROM reviewinfo x WHERE storeinfo.storeno = x.storeno GROUP BY x.storeno ) ,0) AS AVGSCORE , NVL(( SELECT count(reviewno) FROM reviewinfo x WHERE storeinfo.storeno = x.storeno GROUP BY x.storeno ), 0) AS REVIEWCNT FROM storeinfo storeinfo, detailinfo d WHERE storeinfo.storeno = d.storeno
) storeinfo ORDER BY
DESC ) storeinfo
) storeinfo WHERE rnum between 1 and 9

