mapper.insert:

select STORENUM.NEXTVAL from dual

    INSERT INTO STOREINFO(   STORENO
    					   , STORENAME 
   					       , STOREPIC 
   					       , ADDRESS
   					       , HOURS
   					       , CLOSEDDAYS
   					       , CALLNUMBER
   					       , MANAGENO )
                  VALUES(  #{storeNo}
                			, #{storeName} 
                 		   , #{storePic}
                 		   , #{address}
                 		   , #{hours}
                 		   , #{closedDays}
                 		   , #{callNumber}
                 		   , #{manageNo})

